import { useState } from "react";
import "./App.css";

function App() {
  const [users, setUsers] = useState([
    { name: "Aram", age: 36, id: 11 },
    { name: "Emad", age: 40, id: 22 },
    { name: "Elina", age: 3, id: 33 },
  ]);

  const handleClick = (userId) => {
    setUsers(
      users.filter((user) => {
        return user.id !== userId;
      })
    );
  };

  let showListVar = true;
  const showList = () => {
    showListVar = true;
    console.log(showListVar);
    return showListVar;
  };
  const hideList = () => {
    showListVar = false;
    console.log(showListVar);
    return showListVar;
  };

  return (
    <div className="App">
      <div>
        <button onClick={showList}>show</button>
        <button onClick={hideList}>hide</button>
      </div>
      showListVar &&
      {users.map((user, index) => {
        return (
          <div key={index}>
            <h1>
              {user.name}, {user.age}
            </h1>
            <button
              onClick={() => {
                handleClick(user.id);
              }}
            >
              delete
            </button>
          </div>
        );
      })}
    </div>
  );
}

export default App;
