import "./App.css";
import { useState } from "react";

function App() {
  const [courseList, setCourseList] = useState([]);

  const addCourseToList = () => {
    const inputValue = getCourse(event)
    console.log(event.target.value);
    setCourseList(courseList.push(event.target.value));
  };

  let inputValue = "";
  const getCourse = (event) => {
    console.log(event.target.value);
    inputValue = event.target.value;
    return inputValue;
  };

  return (
    <div className="App">
      <div className="addCourse"></div>
      <input
        type="text"
        placeholder="add a course"
        onChange={getCourse}
      ></input>
      <button onClick={addCourseToList}>add</button>
      <div className="courseList">
        <p>{courseList}</p>
      </div>
    </div>
  );
}

export default App;
