import "./App.css";
import { useState } from "react";

function App() {
  const [courseList, setCourseList] = useState([]);
  const addCourseToList = () => {
    setCourseList(courseList.push(getCourse))
  };

  const getCourse = (event) => {
    return event.target.value;
  };

  return <div className="App">
    <div className="addCourse"></div>
    <input type="text" placeholder="add a course" onChange={getCourse}></input>
    <button onClick={addCourseToList}>add</button>
    <div className="courseList">
      <p>{courseList}</p>
    </div>
  </div>;
}

export default App;
