import "./App.css";
import { useState } from "react";

function App() {
  const [showOrNot, setShowOrNot] = useState(true);
  const toggleText = () => {
    setShowOrNot(!showOrNot);
  };

  return (
    <div className="App">
      <button onClick={toggleText}>hide/show</button>
      {showOrNot && <p>I am learning React</p>}
    </div>
  );
}

export default App;
