import "./App.css";
import { useState } from "react";
import CourseComponent from "./CourseComponent";

function App() {
  const [courseList, setCourseList] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [clickedComplete, setClickedComplite] = useState(false);


  const addCourseToList = () => {
    setCourseList([...courseList, inputValue]);
  };

  const getCourse = (event) => {
    setInputValue(event.target.value);
  };

  const deleteCourse = (index) => {
    const newCourseList = [...courseList];
    newCourseList.splice(index, 1);
    setCourseList(newCourseList);
  };

  const compeleteCourse = () => {};

  return (
    <div className="App">
      <div className="addCourse"></div>
      <input
        type="text"
        placeholder="add a course"
        onChange={getCourse}
      ></input>
      <button onClick={addCourseToList}>add</button>
      <div className="courseList">
        {courseList.map((course, index) => {
          return (
            <CourseComponent
              key={index}
              course={course}
              deleteCourse={deleteCourse}
              compeleteCourse={compeleteCourse}
              clickedComplete={clickedComplete}
            />
          );
        })}
      </div>
    </div>
  );
}

export default App;
