const CourseComponent = (props) => {
  return (
    <div>
      {props.course}
      <button
        onClick={() => {
          props.deleteCourse(props.index);
        }}
      >
        delete
      </button>
      <button
        onClick={() => {
          props.handleCompleteCourse(props.index);
        }}
        style={{
          backgroundColor:
            props.courseList[props.index].completed === true && "green",
        }}
      >
        complete
      </button>
    </div>
  );
};

export default CourseComponent;
