import "./App.css";
import { useState } from "react";

function App() {
  const [textColor, setTextColor] = useState("black");
  const changeColor = () => {
    setTextColor();
  };

  return (
    <div className="App">
      <button onClick={changeColor}>change color</button>
      <h1 style={{ color: "{textColor}" }}>I am learning react</h1>
    </div>
  );
}

export default App;
